SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
START TRANSACTION;
SET time_zone = "+00:00";

CREATE TABLE `animals` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `image` varchar(255) NOT NULL,
  `name` varchar(255) NOT NULL,
  `description` text NOT NULL,
  `enclosure_id` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `enclosure_id` (`enclosure_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

INSERT INTO `animals` (`id`, `image`, `name`, `description`, `enclosure_id`) VALUES
(68, 'Public/Le-belvedere/oryx.jpg', 'Oryx beisa', 'L''oryx (Oryx gazella) est un grand mammifère herbivore de la famille des bovidés, vivant principalement dans les régions arides et semi-arides d''Afrique. Il mesure environ 1,2 mètre au garrot pour une longueur de 1,8 à 2,4 mètres et un poids compris entre 180 et 240 kg. Les deux sexes possèdent des cornes longues et droites pouvant atteindre jusqu''à 1,5 mètre. L''oryx est bien adapté aux conditions de sécheresse et peut survivre plusieurs jours sans eau en tirant l''humidité des plantes qu''il consomme. Menacé par la chasse et la perte d''habitat, il est aujourd''hui protégé dans plusieurs réserves et parcs nationaux.', 11),
(69, 'Public/le-belvedere/gazelle.jpg', 'Gazelle',  'La gazelle est un petit mammifère herbivore de la famille des bovidés, vivant principalement dans les savanes, les prairies et les zones désertiques d''Afrique et d''Asie. Elle mesure généralement entre 60 et 100 cm au garrot pour une longueur de 1 à 1,3 mètres et un poids variant entre 20 et 60 kg, selon les espèces. Les gazelles sont reconnues pour leur grande rapidité, capable de courir à des vitesses allant jusqu''à 80 km/h, ce qui leur permet d''échapper aux prédateurs. Elles ont des cornes fines et courbées, présentes principalement chez les mâles, et sont souvent présentes en grands groupes. Les gazelles sont également menacées par la perte d''habitat et la chasse, bien que certaines espèces soient protégées dans des réserves naturelles.', 15),
(70, 'Public/Le-belvedere/autruche.jpg', 'Autruche', 'L''autruche (Struthio camelus) est un grand oiseau non volant originaire d''Afrique, appartenant à la famille des Struthionidae. Elle peut mesurer jusqu''à 2,8 mètres de hauteur et peser entre 100 et 160 kg, ce qui en fait l''oiseau le plus grand et le plus lourd du monde. L''autruche possède de longues jambes puissantes adaptées à la course, capable d''atteindre des vitesses de 70 km/h, et elle utilise ses ailes pour se stabiliser lorsqu''elle court. Bien qu''elle ne puisse pas voler, elle est parfaitement adaptée aux grands espaces ouverts. L''autruche se nourrit principalement de plantes, mais peut aussi consommer des insectes. Menacée par la chasse et la perte d''habitat, elle est protégée dans plusieurs régions.', 15),
(71, 'Public/le-belvedere/gnou.jpg', 'Gnou',  'Le gnou (Connochaetes) est un grand mammifère herbivore de la famille des bovidés, vivant principalement dans les savanes et prairies d''Afrique subsaharienne. Il existe deux espèces principales de gnous : le gnou bleu (Connochaetes taurinus) et le gnou noir (Connochaetes gnou). Le gnou mesure environ 1,2 à 1,4 mètre au garrot pour une longueur de 1,5 à 2,5 mètres et un poids de 150 à 250 kg. Il est facilement reconnaissable par sa tête large, ses cornes recourbées et son corps massif. Les gnous sont des animaux grégaires, vivant en grands troupeaux qui migrent sur de longues distances à la recherche de nourriture et d''eau. Bien que l''espèce ne soit pas immédiatement menacée, certains gnous peuvent être affectés par la perte d''habitat et les perturbations humaines.', 11),
(72, 'Public/Le-belvedere/tapir.jpg', 'Tapir',  'Le tapir est un mammifère herbivore de la famille des Tapiridae, originaire des forêts tropicales d''Amérique centrale et du Sud, ainsi que d''Asie du Sud-Est. Il existe quatre espèces de tapirs : le tapir d''Amérique (Tapirus terrestris), le tapir de Malaisie (Tapirus indicus), le tapir de Baird (Tapirus bairdii) et le tapir de montagne (Tapirus pinchaque). Le tapir mesure généralement entre 1,8 et 2,5 mètres de longueur pour un poids de 150 à 300 kg. Il a un corps massif et une trompe courte, qui est en réalité un prolongement de sa lèvre supérieure, lui permettant de saisir sa nourriture. Les tapirs sont principalement nocturnes et solitaires, et vivent dans des habitats boisés ou marécageux. Ils sont classés comme vulnérables en raison de la déforestation et de la chasse.', 16),
(73, 'Public/Le-belvedere/coati.jpg', 'Coati', 'Le coati (Nasua) est un mammifère de la famille des Procyonidae, vivant en Amérique centrale et du Sud. Il mesure entre 40 et 70 cm de long, avec une queue rayée pouvant atteindre 60 cm. Omnivore, il se nourrit de fruits, d''insectes et de petits animaux. Le coati vit en groupes sociaux et est principalement actif le jour. Bien qu''il ne soit pas menacé, la destruction de son habitat représente une menace pour certaines populations.', 14),
(74, 'Public/Le-belvedere/fennec.jpg', 'Fennec', 'Le fennec (Vulpes zerda) est un petit renard vivant principalement dans les déserts du Sahara et du Moyen-Orient. Il mesure environ 40 cm de long et pèse entre 1 et 1,5 kg. Sa particularité est ses grandes oreilles, qui lui permettent de réguler sa température corporelle. Le fennec est un carnivore nocturne, se nourrissant principalement d''insectes, de petits mammifères et de plantes. Il est parfaitement adapté à la vie dans les zones arides, capable de survivre sans eau pendant plusieurs jours.', 13),
(75, 'Public/Le-belvedere/rhinoceros.jpg', 'Rhinocéros', 'Le rhinocéros est un grand mammifère herbivore appartenant à la famille des Rhinocerotidae. Il existe cinq espèces de rhinocéros, réparties entre l''Afrique et l''Asie. Il peut peser entre 800 kg et 2 300 kg, selon l''espèce, et mesurer jusqu''à 4 mètres de long. Le rhinocéros est facilement reconnaissable par son épaisse peau et ses cornes, qui peuvent mesurer jusqu''à 1,5 mètre. Il vit principalement dans les savanes et les prairies, se nourrissant de graminées, de fruits et de végétation. Menacé par le braconnage pour ses cornes, le rhinocéros est protégé dans plusieurs réserves naturelles.', 11),
(76, 'Public/Le-belvedere/suricate.jpg', 'Suricate', 'Le suricate (Suricata suricatta) est un petit mammifère de la famille des Herpestidae, vivant principalement dans les régions désertiques et semi-désertiques d''Afrique australe. Mesurant environ 25 à 35 cm de long, il pèse entre 0,6 et 1 kg. Le suricate est un animal social qui vit en groupes appelés "mobes", et il est connu pour sa posture caractéristique, où il se dresse sur ses pattes arrière pour surveiller les alentours. Omnivore, il se nourrit principalement d''insectes, de petits animaux et de plantes. Bien que le suricate ne soit pas menacé, il est vulnérable à la perte de son habitat.', 12),
(77, 'Public/Le-belvedere/crocodile.jpg', 'Crocodile nain', 'Le crocodile nain (Crocodylus mindorensis) est une petite espèce de crocodile vivant principalement dans les rivières et marécages de l''île de Mindoro aux Philippines. Il mesure généralement entre 1,5 et 2 mètres de long et pèse environ 5 à 7 kg. Ce crocodile se distingue par sa taille réduite et sa couleur brune ou grise, qui lui permet de se camoufler dans son environnement aquatique. Carnivore, il se nourrit principalement de poissons, d''insectes et de petits animaux. Menacé par la destruction de son habitat et la chasse, il est classé comme espèce vulnérable.', 19),
(78, 'Public/Le-belvedere/casoar.jpg', 'Casoar', 'Le casoar (Casuarius) est un grand oiseau non volant originaire des forêts tropicales de l''Australie, de la Nouvelle-Guinée et des îles environnantes. Il mesure jusqu''à 1,8 mètre de hauteur et peut peser jusqu''à 85 kg. Le casoar est reconnaissable par sa crête en forme de casque sur la tête et sa peau bleue et rouge autour du cou. Cet oiseau est principalement frugivore, se nourrissant de fruits tombés au sol. Bien qu''il soit généralement solitaire et réservé, le casoar peut devenir très agressif lorsqu''il se sent menacé. Il est classé comme vulnérable en raison de la destruction de son habitat.', 18),
(79, 'Public/Le-belvedere/guepard.jpg', 'Guépard', 'Le guépard (Acinonyx jubatus) est un grand félin vivant principalement en Afrique et dans certaines régions d''Asie. Il mesure environ 1,1 à 1,5 mètre de long et pèse entre 40 et 70 kg. Le guépard est célèbre pour sa vitesse de course, atteignant jusqu''à 110 km/h, ce qui en fait l''animal terrestre le plus rapide. Il possède un pelage tacheté caractéristique et est un chasseur solitaire, se nourrissant principalement de gazelles et d''autres petits herbivores. Menacé par la perte d''habitat et le braconnage, le guépard est classé vulnérable.', 17),
(80, 'Public/Le-belvedere/saimiri.jpg', 'Saïmiri', 'Le saïmiri (Saimiri sciureus), également appelé "singe écureuil", est un petit primate de la famille des Cebidae, vivant principalement dans les forêts tropicales d''Amérique centrale et du Sud. Il mesure environ 40 cm de long, avec une queue longue et touffue. Le saïmiri est un animal social, vivant en groupes pouvant atteindre plusieurs centaines d''individus. Il se nourrit principalement de fruits, d''insectes et de petits vertébrés. Bien que le saïmiri ne soit pas actuellement menacé, la déforestation et la perte de son habitat représentent une menace pour certaines populations.', 14),
(81, 'Public/Le-plateau/lionne.jpg', 'Lion', 'Le lion ( Panthera leo), un mammifère carnivore de la famille des félidés, vit principalement dans les savanes, les prairies et les zones forestières d Afrique subsaharienne, avec une petite population en Inde (le lion d Asie). Il mesure entre 1,2 et 1,5 m au garrot, et de 1,7 à 2,5 m de longueur, pesant entre 120 et 190 kg pour les femelles et 150 à 250 kg pour les mâles. Sa durée de vie varie de 10 à 14 ans en milieu sauvage, et peut atteindre jusqu à 20 ans en captivité. La gestation dure 110 jours, avec 1 à 4 petits par portée. Le lion est un carnivore qui chasse des proies de taille moyenne à grande. Classé vulnérable (VU) par l UICN, l espèce est menacée principalement par la perte d habitat et le braconnage.', 31),
(82, 'Public/Le-plateau/Hippopotame.jpg', 'Hippopotame', 'L''hippopotame (Hippopotamus amphibius) est un grand mammifère herbivore vivant principalement dans les rivières et lacs d''Afrique subsaharienne. Il mesure entre 3,5 et 5 mètres de long pour un poids pouvant atteindre 4 500 kg. L''hippopotame possède une peau épaisse et un grand corps massif, mais il est également un excellent nageur et peut passer beaucoup de temps sous l''eau. Bien qu''il passe la majeure partie de son temps dans l''eau pour réguler sa température, il se nourrit principalement de végétation terrestre la nuit. L''hippopotame est considéré comme un animal dangereux en raison de son comportement territorial et de sa grande force.', 30),
(83, 'Public/Le-plateau/hyène.jpg', 'Hyène', 'La hyène (Hyaenidae) est un carnivore de la famille des Hyaenidae, vivant principalement en Afrique et dans certaines régions d''Asie. Il existe quatre espèces de hyènes, dont la hyène tachetée (Crocuta crocuta), la plus connue. La hyène peut mesurer entre 90 cm et 1,5 mètre de long, avec un poids variant de 40 à 80 kg. Cet animal est réputé pour son cri caractéristique ressemblant à un rire, mais il est également un redoutable prédateur et charognard. La hyène vit en groupes sociaux complexes et se nourrit principalement de viande, chassant parfois en groupe ou en profitant des carcasses laissées par d''autres prédateurs.', 27),
(84, 'Public/Le-plateau/elephant.jpg', 'Éléphant', 'L''éléphant (Elephantidae) est le plus grand mammifère terrestre, vivant principalement en Afrique et en Asie. Il existe deux espèces principales : l''éléphant d''Afrique (Loxodonta africana) et l''éléphant d''Asie (Elephas maximus). L''éléphant peut mesurer jusqu''à 4 mètres de haut pour un poids pouvant atteindre 6 000 kg. Il est facilement reconnaissable par ses grandes oreilles, sa trompe et ses défenses. Herbivore, l''éléphant se nourrit de feuilles, d''écorce, de fruits et d''herbe. Il joue un rôle écologique important, notamment en dispersant des graines et en modifiant les paysages. Menacé par le braconnage et la perte d''habitat, l''éléphant est protégé dans plusieurs régions.', 26),
(85, 'Public/Le-plateau/plateau.jpg', 'Girafe', 'La girafe (Giraffa camelopardalis) est le plus grand mammifère terrestre, vivant principalement en Afrique. Elle peut atteindre jusqu''à 5,5 mètres de hauteur et peser jusqu''à 1 200 kg. Sa longue nuque et ses pattes lui permettent de brouter des feuilles dans les arbres. La girafe a un pelage tacheté unique, qui varie d''une sous-espèce à l''autre. Herbivore, elle se nourrit principalement de feuilles d''acacia. Bien que non menacée, elle subit des pressions liées à la perte d''habitat.', 25),
(86, 'Public/Le-plateau/varankomodo.jpg', 'Varan de Komodo', 'Le varan de Komodo (Varanus komodoensis) est un grand reptile carnivore endémique des îles Komodo en Indonésie. Il peut atteindre 3 mètres de long et peser jusqu''à 70 kg. Ce prédateur au métabolisme lent chasse des proies telles que des cerfs et des sangliers. Il utilise sa salive toxique pour affaiblir ses proies. Menacé par la perte de son habitat, il est protégé par des réserves naturelles.', 24),
(87, 'Public/Le-plateau/zebre.jpg', 'Zèbre', 'Le zèbre (Equus quagga) est un cheval sauvage africain, reconnaissable à ses rayures noires et blanches. Il vit principalement dans les savanes et les prairies. Le zèbre mesure environ 1,4 à 1,5 mètre au garrot et pèse entre 300 et 400 kg. Herbivore, il se nourrit principalement d''herbes. Les zèbres vivent en groupes sociaux et sont connus pour leur capacité à se défendre contre les prédateurs grâce à leur rapidité et leurs puissantes morsures.', 29),
(88, 'Public/Le-plateau/grivet.png', 'Grivet', 'Le grivet (Chlorocebus aethiops) est un petit singe de la famille des cercopithécidés, vivant principalement en Afrique de l''Est. Il mesure environ 50 cm de long, avec une queue longue et touffue. Ce primate omnivore se nourrit de fruits, d''insectes et de petits animaux. Le grivet est souvent trouvé en groupes sociaux et est connu pour son agilité. Bien qu''il ne soit pas menacé, la déforestation affecte ses populations.', 23),
(89, 'Public/Le-plateau/cerco.jpg', 'Cercopithèque', 'Le cercopithèque est un genre de singes appartenant à la famille des Cercopithecidae, vivant principalement en Afrique. Ce primate mesure entre 40 et 70 cm de long, avec une queue longue et agile. Il est omnivore, se nourrissant de fruits, de feuilles et d''insectes. Les cercopithèques vivent en groupes sociaux complexes et sont connus pour leur intelligence. Certaines espèces sont menacées par la perte de leur habitat.', 23),
(90, 'Public/Le-plateau/loupcriniere.png', 'Loup à crinière', 'Le loup à crinière (Chrysocyon brachyurus) est un canidé rare vivant principalement en Amérique du Sud, dans les prairies et savanes. Il mesure environ 75 cm de haut au garrot et pèse entre 20 et 25 kg. Ce loup se distingue par sa crinière et ses longues pattes. Carnivore et omnivore, il se nourrit de petits animaux, de fruits et de racines. Menacé par la perte d''habitat, il est classé vulnérable.', 28),
(91, 'Public/Le-plateau/tamarin.jpg', 'Tamarin', 'Le tamarin est un petit singe arboricole vivant principalement dans les forêts tropicales d''Amérique centrale et du Sud. Il mesure environ 25 à 40 cm de long, avec une queue longue et touffue. Le tamarin est omnivore, se nourrissant de fruits, d''insectes et de petits vertébrés. Il vit en groupes sociaux et est connu pour son agilité et ses comportements sociaux. Certaines espèces de tamarins sont menacées par la déforestation.', 20),
(92, 'Public/Le-plateau/capucin.jpg', 'Capucin', 'Le capucin est un primate de la famille des Cebidae, vivant principalement en Amérique centrale et du Sud. Il mesure environ 40 à 50 cm de long, avec une queue longue et agile. Ce singe est omnivore, se nourrissant de fruits, d''insectes et de petits animaux. Très intelligent, il est utilisé dans certaines régions pour la récolte de fruits. Certaines espèces de capucins sont menacées par la déforestation et la chasse.', 21),
(93, 'Public/Le-plateau/gibbon.jpg', 'Gibbon', 'Le gibbon est un petit primate de la famille des Hylobatidae, vivant principalement dans les forêts tropicales d''Asie du Sud-Est. Il mesure environ 50 à 90 cm de long et a une longue queue préhensile. Les gibbons sont des singes arboricoles, connus pour leur capacité à se balancer d''un arbre à l''autre. Ils sont principalement frugivores, se nourrissant de fruits et de feuilles. Ils sont menacés par la déforestation et la chasse.', 22),
(94, 'Public/Le-plateau/ouistiti.jpg', 'Ouistiti', 'Louistiti commun ( Callithrix jacchus), un primate de la famille des callitrichidés, vit principalement dans les forêts tropicales et sèches du Brésil. Il mesure entre 18 et 25 cm sans la queue et pèse de 300 à 500 g. Sa durée de vie varie de 10 à 12 ans en milieu sauvage, mais peut atteindre jusqu à 18 ans en captivité. La gestation dure environ 5 mois, donnant naissance à 2 petits par portée. Omnivore, il se nourrit de fruits, d insectes et de petits vertébrés. Classé *préoccupation mineure* (LC) par l UICN, il n est pas actuellement en danger.', 22),
(95, 'Public/les-clairieres/marabout.jpg', 'Marabout', 'Le marabout (Leptoptilos crumeniferus) est un grand oiseau de la famille des Ciconiidae, vivant principalement en Afrique. Il mesure jusqu''à 1,5 mètre de haut et possède un long bec courbé. Ce charognard se nourrit principalement de carcasses, de poissons et de petits animaux. Le marabout est facilement reconnaissable à son plumage sombre et sa tête dénudée. Bien qu''il ne soit pas menacé, son habitat est parfois perturbé par les activités humaines.', 32),
(96, 'Public/les-clairieres/watusi.jpg', 'Watusi', 'Le Watusi est une race de bovin originaire d''Afrique, connue pour ses cornes massives qui peuvent atteindre 2,5 mètres de long. Ces animaux sont élevés principalement pour leur lait et leur viande. Ils mesurent environ 1,5 mètre au garrot et pèsent entre 600 et 800 kg. Le Watusi est parfaitement adapté à la vie dans les régions chaudes et arides. Ils sont utilisés dans certaines cultures africaines pour des rituels traditionnels.', 33),
(97, 'Public/les-clairieres/emeu.jpg', 'Émeu', 'L''éméu (Dromaius novaehollandiae) est un grand oiseau non volant originaire d''Australie. Il peut atteindre jusqu''à 1,9 mètre de hauteur et peser jusqu''à 55 kg. L''éméu est un oiseau omnivore qui se nourrit de plantes, d''insectes et de petits animaux. Bien qu''il ne puisse pas voler, il est un excellent coureur, capable de courir à des vitesses de 50 km/h. Il vit dans les savanes et les forêts ouvertes et est classé comme une espèce de moindre préoccupation.', 42),
(98, 'Public/les-clairieres/bison.jpg', 'Bison', 'Le bison (Bison bison) est un grand mammifère herbivore originaire d''Amérique du Nord et d''Europe. Il mesure environ 1,8 mètre au garrot et peut peser entre 400 et 900 kg. Le bison est un herbivore qui se nourrit principalement d''herbes et de plantes. Bien qu''il ait été proche de l''extinction à cause de la chasse excessive, il est aujourd''hui protégé et a été réintroduit dans plusieurs parcs naturels.', 45),
(99, 'Public/les-clairieres/dromadaire.jpg', 'Dromadaire', 'Le dromadaire (Camelus dromedarius) est un grand mammifère herbivore vivant principalement dans les déserts d''Afrique et du Moyen-Orient. Il mesure jusqu''à 2 mètres de haut et peut peser entre 400 et 600 kg. Il est reconnu pour sa capacité à survivre sans eau pendant de longues périodes, en stockant l''eau dans ses graisses. Le dromadaire est utilisé comme bête de somme dans les régions arides, où il est essentiel pour le transport et le commerce.', 46),
(100, 'Public/les-clairieres/flamant.jpg', 'Flamant rose', 'Le flamant rose (Phoenicopterus roseus) est un grand oiseau aquatique vivant principalement dans les lagunes et les marais salants. Il mesure environ 1,4 mètre de haut et pèse entre 3 et 4 kg. Le flamant rose est reconnu pour ses plumes roses et son long cou courbé. Il se nourrit principalement de petits invertébrés et de plancton qu''il filtre à l''aide de son bec spécialisé. Bien qu''il soit commun, il est menacé par la destruction de ses habitats.', 39),
(101, 'Public/les-clairieres/cigogne.jpg', 'Cigogne', 'La cigogne (Ciconia ciconia) est un grand oiseau migrateur de la famille des Ciconiidae, vivant en Europe, en Asie et en Afrique. Elle mesure jusqu''à 1,4 mètre de hauteur et possède une envergure pouvant atteindre 2,2 mètres. Elle se nourrit principalement de petits animaux, de poissons et d''insectes. La cigogne est souvent associée à la symbolique de la fertilité. Bien qu''elle ne soit pas menacée, elle subit des pressions liées à la perte de ses habitats de reproduction.', 32),
(102, 'Public/les-clairieres/oryx.jpg', 'Oryx algazelle', 'L''oryx algazelle (Oryx algazelle) est une espèce de grand mammifère herbivore vivant principalement dans les régions désertiques du nord de l''Afrique. Il mesure environ 1,1 mètre au garrot et pèse entre 200 et 250 kg. Cet oryx a des cornes droites pouvant atteindre 1,5 mètre de long. Adapté aux environnements arides, il peut survivre sans eau pendant plusieurs jours en tirant l''humidité des plantes qu''il mange.', 33),
(103, 'Public/les-clairieres/ane.jpg', 'Âne de Somalie', 'L''âne de Somalie (Equus africanus asinus) est une sous-espèce d''âne vivant dans les régions arides de la Corne de l''Afrique. Il mesure environ 1 mètre de haut au garrot et pèse entre 200 et 300 kg. Cet âne est caractérisé par son pelage gris clair et ses oreilles longues. Il est utilisé comme animal de travail dans les régions désertiques et semi-arides. L''âne de Somalie est menacé en raison de la perte de son habitat et de la chasse.', 33),
(104, 'Public/les-clairieres/yack.jpg', 'Yack', 'Le yack (Bos grunniens) est un grand bovin originaire des montagnes de l''Himalaya. Il mesure environ 1,5 à 2 mètres de haut au garrot et pèse entre 400 et 1 000 kg. Le yack est un herbivore qui se nourrit principalement de plantes alpines et de lichens. Il est bien adapté aux conditions froides et peut survivre dans des températures extrêmement basses. Utilisé pour le transport et comme source de lait et de viande, le yack est essentiel à la vie dans les régions montagneuses de l''Asie centrale.', 38),
(105, 'Public/les-clairieres/mouton.jpg', 'Mouton noir', 'Le mouton noir est une variété de mouton domestique, caractérisé par son pelage noir. Ce mouton est élevé principalement pour sa laine et sa viande. Il mesure environ 80 à 90 cm au garrot et pèse entre 40 et 80 kg. Bien qu''il soit une race domestique, le mouton noir a une histoire longue, étant élevé depuis des siècles pour des usages variés. Sa laine est appréciée pour sa douceur et sa couleur particulière.', 38),
(106, 'Public/les-clairieres/ibis.jpg', 'Ibis', 'L''ibis (Threskiornithidae) est un oiseau au long bec courbé, vivant dans les régions marécageuses et les zones humides d''Afrique, d''Asie et d''Amérique. Il se nourrit principalement de petits animaux aquatiques, de crustacés et de plantes. L''ibis est souvent associé à des symboles de sagesse et de mysticisme dans différentes cultures. Plusieurs espèces d''ibis sont menacées par la perte de leurs habitats naturels.', 34),
(107, 'Public/les-clairieres/tortue.jpg', 'Tortue', 'La tortue d’Hermann (Testudo hermanni), un reptile de l’ordre des testudinates et de la famille des testudinidés, est une espèce terrestre originaire des régions méditerranéennes, du sud-est de l’Europe et d’Afrique du Nord. Avec une taille de 20 à 30 cm et un poids de 1 à 3 kg, cette tortue herbivore se nourrit principalement de plantes, feuilles et fleurs. Elle vit généralement entre 50 et 100 ans, pond 3 à 6 œufs par portée après une gestation de 60 à 90 jours, et habite les prairies, forêts ouvertes et zones arides. Son statut UICN est quasi menacé (NT).', 34),
(108, 'Public/les-clairieres/pecari.jpg', 'Pécari', 'Le pécari (Tayassuidae) est un petit mammifère de la famille des Tayassuidés, vivant principalement en Amérique du Sud et centrale. Il mesure environ 1,2 mètre de long et pèse entre 20 et 40 kg. Il possède un pelage brun foncé et une ligne de dos hérissée. Le pécari est un animal omnivore qui se nourrit de fruits, de racines, d''insectes et de petits animaux. Il vit en groupes et est parfois considéré comme une espèce de moindre préoccupation, bien qu''il soit menacé par la perte de son habitat.', 37),
(109, 'Public/les-clairieres/tamanoir.jpg', 'Tamanoir', 'Le tamanoir (Myrmecophaga tridactyla) est un mammifère insectivore originaire d''Amérique du Sud. Il est caractérisé par son long museau, ses griffes puissantes et son pelage grossier. Il mesure jusqu''à 1,8 mètre de long, y compris sa queue, et pèse entre 20 et 40 kg. Le tamanoir se nourrit principalement de fourmis et de termites. Bien qu''il ne soit pas en danger immédiat, il est vulnérable à la déforestation et à la chasse.', 39),
(110, 'Public/les-clairieres/nandou.jpg', 'Nandou', 'Le nandou (Rhea) est un grand oiseau originaire d''Amérique du Sud, semblable à l''autruche, mais plus petit. Il mesure environ 1,5 mètre de hauteur et pèse entre 20 et 30 kg. Le nandou est un oiseau non volant qui court rapidement, atteignant des vitesses de 60 km/h. Il vit dans les plaines et les savanes et se nourrit principalement de plantes, de graines et de petits animaux. Le nandou est une espèce de moindre préoccupation mais subit des pressions à cause de la chasse.', 39),
(111, 'Public/les-clairieres/wallaby.jpg', 'Wallaby', 'Le wallaby est un petit marsupial de la famille des Macropodidae, originaire d''Australie et de Nouvelle-Guinée. Il mesure entre 40 et 90 cm de hauteur et pèse entre 10 et 20 kg. Le wallaby se distingue par sa taille plus petite que celle du kangourou. Herbivore, il se nourrit principalement de plantes herbacées et de fruits. Il vit dans des habitats variés, allant des forêts aux prairies. Bien qu''il ne soit pas en danger, certaines espèces de wallabies sont menacées.', 42),
(112, 'Public/les-clairieres/porcepic.jpg', 'Porc-épic', 'Le porc-épic (Erethizon dorsatum) est un grand rongeur couvert de piquants rigides, vivant principalement en Amérique du Nord. Il mesure entre 60 et 90 cm de long et pèse entre 5 et 15 kg. Herbivore, il se nourrit principalement d''écorce, de racines et de fruits. Bien que le porc-épic soit relativement lent et inoffensif, ses piquants peuvent infliger des blessures graves en cas d''attaque. Il est menacé par la perte de son habitat et la chasse.', 44),
(113, 'Public/les-clairieres/anne.jpg', 'Âne de Provence', 'L''âne de Provence est une race d''âne originaire du sud de la France, particulièrement adaptée aux climats chauds et secs. Il mesure environ 1,2 mètre au garrot et pèse entre 180 et 250 kg. Cet âne est caractérisé par un pelage court et gris, avec des oreilles longues et une tête fine. Il est utilisé comme bête de somme et pour la production de lait. Bien que la race soit en danger en raison de la baisse de sa population, des efforts de conservation sont en place.', 46),
(114, 'Public/tiger.jpg', 'Tigre', 'Le tigre (Panthera tigris) est un grand carnivore de la famille des Félidés, vivant principalement en Asie. Il mesure jusqu''à 3 mètres de long et pèse entre 100 et 300 kg, selon les sous-espèces. Le tigre est un prédateur solitaire, chassant principalement des ongulés et des animaux plus petits. Il est facilement reconnaissable à son pelage rayé. Menacé par la perte de son habitat et la chasse illégale, le tigre est inscrit sur la liste des espèces en danger.', 41),
(115, 'Public/les-clairieres/chiensbuissons.png', 'Chien des buissons', 'Le chien des buissons (Lycaon pictus) est un prédateur social vivant principalement en Afrique subsaharienne. Il mesure environ 1 mètre de long et pèse entre 20 et 30 kg. Ce carnivore vit en meutes et chasse des proies plus grandes comme des impalas et des gazelles. Il est facilement reconnaissable par son pelage tacheté. Bien que sa population soit en déclin en raison de la perte de son habitat et des maladies, le chien des buissons est protégé dans certaines réserves.', 40),
(116, 'Public/les-clairieres/serval.jpg', 'Serval', 'Le serval (Leptailurus serval) est un félin de taille moyenne, vivant principalement en Afrique subsaharienne. Il mesure entre 60 et 90 cm de haut au garrot et pèse entre 9 et 18 kg. Le serval est un prédateur spécialisé dans la chasse aux petits mammifères, oiseaux et insectes. Il est facilement reconnaissable à ses grandes oreilles et à son pelage tacheté. Bien que sa population soit stable, il peut être menacé par la perte de son habitat naturel.', 36),
(117, 'Public/les-clairieres/lynx.jpg', 'Lynx', 'Le lynx (Lynx lynx) est un grand félin vivant principalement en Europe, en Asie et en Amérique du Nord. Il mesure environ 70 cm au garrot et pèse entre 18 et 30 kg. Le lynx est un prédateur nocturne qui se nourrit principalement de cerfs, de rongeurs et d''oiseaux. Il est facilement reconnaissable à ses touffes de poils sur les oreilles et à sa queue courte. Bien que le lynx soit protégé dans de nombreuses régions, il peut être menacé par la perte de son habitat et la chasse.', 35),
(118, 'Public/bois-pins-photo/antilope.jpeg', 'Antilope', 'Les antilopes, appartenant à la classe des mammifères, à l’ordre des artiodactyles et à la famille des bovidés, regroupent des espèces variées comme l antilope saïga et l antilope d Afrique. Ces herbivores se nourrissent principalement d’herbes, feuilles et pâturages. Originaires des savanes, plaines et zones semi-arides d’Afrique, d’Asie et de certaines régions d’Europe, elles mesurent entre 0,6 et 1,5 m au garrot et pèsent de 20 à 300 kg selon les espèces. Leur durée de vie est de 10 à 20 ans en milieu sauvage, et les femelles donnent naissance à 1 ou 2 petits après environ 180 jours de gestation. Le statut UICN varie selon les espèces, allant de vulnérable à en danger.', 50),
(119, 'Public/bois-pins-photo/daim.jpg', 'Daim', 'Le daim (Dama dama), un mammifère de l’ordre des artiodactyles et de la famille des cervidés, est un herbivore originaire dEurope et d Asie mineure. Il vit dans les forêts mixtes, prairies et parcs, où il se nourrit principalement d herbes, feuilles, fruits et écorces. Mesurant entre 0,8 et 1,0 m au garrot et pesant de 30 à 100 kg, il a une durée de vie de 12 à 16 ans en milieu sauvage. La gestation dure environ 230 jours et donne naissance à 1 ou 2 petits par portée. Classé comme de préoccupation mineure par l UICN, l espèce est cependant sensible à la dégradation de son habitat.', 50),
(120, 'Public/bois-pins-photo/nilgaut.jpg', 'Nilgaut', 'Le nilgaut (Boselaphus tragocamelus), également connu sous le nom d antilope bleue, est un mammifère de l’ordre des artiodactyles et de la famille des bovidés. Originaire du sous-continent indien, il habite les plaines ouvertes, prairies et forêts claires. Cet herbivore, qui se nourrit principalement d’herbes, feuilles et jeunes pousses, mesure entre 1,2 et 1,5 m au garrot pour un poids de 100 à 300 kg. Sa durée de vie en milieu sauvage est de 12 à 15 ans, et les femelles mettent bas 1 à 2 petits après une gestation d’environ 240 jours. Actuellement, le nilgaut est classé comme étant de préoccupation mineure par l’UICN.', 50),
(121, 'Public/bois-pins-photo/macaque-crabier.jpg', 'Crabier', 'Le macaque à longue queue (Macaca fascicularis), un mammifère de l’ordre des primates et de la famille des cercopithécidés, est une espèce omnivore se nourrissant de fruits, insectes et petits crustacés. Originaire des forêts tropicales, mangroves et zones humides d’Asie du Sud-Est (Philippines, Indonésie, Malaisie), il mesure entre 40 et 60 cm de longueur pour un poids de 3 à 9 kg selon le sexe. Avec une durée de vie de 20 à 30 ans en milieu sauvage, les femelles donnent naissance à un petit après une gestation de 160 à 170 jours. Actuellement, cette espèce est classée comme étant de préoccupation mineure (LC) par l’UICN.', 47),
(122, 'Public/bois-pins-photo/vautour.jpg', 'Vautour', 'Les vautours, appartenant à l ordre des accipitriformes et à la famille des accipitridés, sont des oiseaux nécrophages qui se nourrissent principalement de carcasses d animaux. Originaires d Europe, d Asie et d Afrique, ces rapaces mesurent entre 2 et 3 mètres d envergure pour un poids de 5 à 12 kg. Leur durée de vie en milieu sauvage est de 20 à 30 ans, et la période de nidification dure environ 50 jours, avec généralement un jeune par ponte. Ils habitent les montagnes, falaises et régions arides. Le statut UICN des vautours varie selon l espèce, allant de quasi menacé à en danger critique.', 49),
(123, 'Public/bois-pins-photo/loup-europeen.jpg', 'Loup d Europe', 'Le loup européen (Canis lupus lupus), un mammifère de l’ordre des carnivores et de la famille des canidés, est une sous-espèce du loup gris. Mesurant entre 0,6 et 0,8 m au garrot pour un poids de 25 à 40 kg, il vit de 6 à 8 ans en milieu sauvage et jusqu’à 16 ans en captivité. Carnivore, il se nourrit principalement d’ongulés, de petits mammifères et, occasionnellement, de fruits. Originaire des forêts, montagnes, plaines et toundras d’Europe et d’Eurasie, la femelle met bas 4 à 6 petits après une gestation d’environ 63 jours. Classé de préoccupation mineure par l’UICN, il reste néanmoins vulnérable dans certaines régions en raison de la chasse et de la fragmentation de son habitat.', 51),
(124, 'Public/bois-pins-photo/cerf.jpg', 'Cerf', 'Le cerf élaphe (Cervus elaphus), un mammifère de l’ordre des artiodactyles et de la famille des cervidés, est une espèce herbivore se nourrissant de feuilles, herbes et rameaux. Originaire des forêts tempérées, prairies et zones montagneuses d’Europe, d’Asie et d’Amérique du Nord, il mesure entre 1,2 et 1,5 m au garrot et pèse de 100 à 300 kg selon les régions. Sa durée de vie en milieu sauvage est de 10 à 15 ans, et les femelles mettent bas un seul faon après une gestation de 230 jours. Le cerf élaphe est actuellement classé comme étant de préoccupation mineure (LC) par l’UICN.', 48),
(125, 'Public/Bergerie-des-reptiles/python.jpg', 'Python', 'Le python sebae, un reptile appartenant à l ordre des squamates et à la famille des boidés, est l un des plus grands serpents d Afrique, mesurant entre 4 et 6 mètres pour un poids de 50 à 70 kg. Originaire des forêts tropicales humides d Afrique centrale et occidentale, ce carnivore se nourrit principalement de mammifères et d oiseaux. Il a une espérance de vie de 20 à 30 ans en captivité, une période de gestation de 90 à 100 jours, et pond 20 à 40 œufs par portée. Actuellement, l espèce est classée comme non menacée (LC) par l UICN.', 1),
(126, 'Public/vallon-cascades-photo/tortue2.jpg', 'Tortue', 'La tortue d’Hermann (Testudo hermanni), un reptile de l’ordre des testudinates et de la famille des testudinidés, est une espèce terrestre originaire des régions méditerranéennes, du sud-est de l’Europe et d’Afrique du Nord. Avec une taille de 20 à 30 cm et un poids de 1 à 3 kg, cette tortue herbivore se nourrit principalement de plantes, feuilles et fleurs. Elle vit généralement entre 50 et 100 ans, pond 3 à 6 œufs par portée après une gestation de 60 à 90 jours, et habite les prairies, forêts ouvertes et zones arides. Son statut UICN est quasi menacé (NT).', 1),
(127, 'Public/Bergerie-des-reptiles/iguane.jpg', 'Iguane', 'L iguane vert (Iguana iguana), un reptile de l’ordre des squamates et de la famille des iguanidés, est une espèce herbivore originaire des forêts tropicales humides d’Amérique centrale et du Sud. Mesurant entre 1,5 et 2 mètres de longueur pour un poids de 4 à 5 kg, il se nourrit principalement de feuilles, fleurs et fruits. En captivité, il vit généralement entre 20 et 30 ans, avec une gestation de 2 à 3 mois et des portées de 20 à 40 œufs. Actuellement, cette espèce est classée comme non menacée (LC) par l’UICN.', 1),
(128, 'Public/vallon-cascades-photo/panthere.jpg', 'Panthère', 'La panthère (Panthera pardus) est un grand félin vivant principalement en Afrique et en Asie. Elle mesure entre 1,2 et 1,5 mètre de long et pèse entre 30 et 90 kg. Son pelage est tacheté de rosettes sombres, bien qu''elle puisse aussi être noire (panthère noire). Carnivore, elle chasse des mammifères de taille moyenne et est excellente grimpeuse. Menacée par la perte d''habitat et la chasse, elle est classée vulnérable dans certaines régions.', 4),
(129, 'Public/vallon-cascades-photo/ara.jpg', 'Ara Perroquet', 'L''ara (Ara) est un genre de grands perroquets colorés vivant principalement en Amérique centrale et du Sud. Ils sont connus pour leurs plumages vibrants, souvent dans des teintes de bleu, vert, rouge et jaune. Les aras sont des oiseaux sociaux qui vivent en groupes et se nourrissent principalement de fruits, de noix et de graines. Ils sont des symboles de la biodiversité tropicale, mais malheureusement, plusieurs espèces d''aras sont en danger en raison de la déforestation et du commerce illégal d''animaux.', 2),
(130, 'Public/vallon-cascades-photo/grandhocco.jpg', 'Grand Hocco', 'Le grand hocco (Crax rubra) est un grand oiseau de la famille des Cracidae, originaire des forêts tropicales d''Amérique centrale et du Sud. Il mesure environ 1 mètre de long et pèse entre 2 et 4 kg. Le grand hocco est un oiseau au plumage rouge et noir, avec une crête distinctive sur la tête. C''est un animal principalement terrestre qui se nourrit de fruits, de graines et d''insectes. Menacé par la déforestation et la chasse, le grand hocco est une espèce protégée dans certaines zones de son habitat naturel.', 3),
(131, 'Public/vallon-cascades-photo/panda-roux.jpg', 'Panda roux', 'Le panda roux (Ailurus fulgens), un mammifère de l ordre des carnivores et de la famille des ailuridés, est un omnivore originaire des forêts d haute montagne et des zones forestières de l Himalaya, de la Chine, du Népal et du Bhoutan. Mesurant entre 50 et 64 cm de long pour un poids de 3 à 6 kg, il vit généralement de 8 à 10 ans en milieu sauvage. Il se nourrit principalement de bambou, mais aussi de fruits et d insectes. Sa gestation dure entre 120 et 150 jours, avec une portée de 1 à 4 petits. Actuellement, l espèce est classée en danger par l UICN.', 5),
(132, 'Public/vallon-cascades-photo/lemurien.jpg', 'Lémurien', 'Le lémurien (Lemur spp.), un primate de la famille des lémuriidés, est une espèce omnivore originaire des forêts de Madagascar. Mesurant entre 40 et 60 cm de long pour un poids de 2 à 4 kg, il vit de 20 à 30 ans en milieu sauvage. Son régime alimentaire est varié, composé de fruits, feuilles et insectes. La gestation dure entre 60 et 70 jours, donnant naissance à 1 ou 2 petits par portée. Le lémurien est une espèce emblématique de Madagascar, où il occupe les forêts tropicales de l île.', 7),
(133, 'Public/vallon-cascades-photo/chevre-naine.jpg', 'Chèvre naine', 'La chèvre (Capra hircus), un mammifère de l ordre des artiodactyles et de la famille des bovidés, est un herbivore originaire d Afrique, d Asie et d Europe. Elle vit dans les prairies et les collines, où elle se nourrit principalement d herbes et de feuilles. Mesurant entre 40 et 50 cm de hauteur et pesant de 15 à 30 kg, elle a une durée de vie de 10 à 15 ans. La gestation dure environ 150 jours, et les femelles mettent bas de 1 à 3 chevreaux par portée. La chèvre est classée comme quasi menacée (NT) par l UICN, en raison des menaces liées à la perte d habitat et à la pression de la domestication.', 6),
(134, 'Public/vallon-cascades-photo/loutre.jpg', 'Loutre', 'La loutre européenne (Lutra lutra), un mammifère carnivore de la famille des mustélidés, vit principalement dans les rivières, lacs et littoraux d Europe et d Asie. Elle mesure entre 90 et 130 cm de longueur et pèse entre 7 et 12 kg. Son régime alimentaire est composé principalement de poissons et de crustacés. La loutre européenne a une durée de vie de 10 à 15 ans, et la gestation dure entre 60 et 65 jours, donnant naissance à 1 à 5 petits par portée. Classée *moins préoccupante* (LC) par l UICN, elle n est pas actuellement menacée, bien que ses populations soient parfois fragilisées par la pollution et la dégradation de son habitat.', 10),
(135, 'Public/vallon-cascades-photo/mouflon.jpeg', 'Mouflon', 'Le mouflon (Ovis orientalis) est un mouton sauvage originaire des montagnes de l''Europe et de l''Asie. Il se caractérise par ses grandes cornes enroulées, présentes chez les mâles, et son pelage généralement brun à rougeâtre. Le mouflon vit en troupeaux dans les régions montagneuses et boisées, où il se nourrit principalement de végétation herbacée. Bien qu''il ne soit pas considéré comme une espèce en danger, il est protégé dans certaines régions en raison de la chasse excessive et de la perte d''habitat.', 9),
(136, 'Public/vallon-cascades-photo/binturong.jpg', 'Binturong', 'Le binturong (Arctictis binturong), un mammifère carnivore de la famille des viverridés, est un omnivore originaire des forêts humides d Asie du Sud-Est. Il mesure entre 60 et 95 cm de longueur et pèse de 9 à 20 kg. Sa durée de vie en milieu sauvage est de 20 à 25 ans. La gestation dure entre 90 et 100 jours, avec 1 à 3 petits par portée. Son régime alimentaire est varié, comprenant des fruits, des insectes et des œufs. L espèce est classée *vulnérable* (VU) par l UICN, en raison de la déforestation et de la chasse illégale qui menacent ses populations.', 10);

-- Structure de la table `enclosures`
